package lecture_3;
import java.util.*;

public class catch_the_coin_codeforces {

	public static void main(String[] args) {
//		 TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		while(n > 0) {
			int x = sc.nextInt();
			int y = sc.nextInt();
			if(y <= -1) {
				System.out.println("No");
			}else
				System.out.println("Yes");
		}

	}

}
